        int8 cuenta=0;
        for(int8 i = 0; i < preguntas.length; i++) {
            if( lalistaderespuestas[i].usuario == msg.sender){
                acumulador += lalistaderespuestas[i].puntaje
                cuenta ++;
            }
        }
        return acumulador/cuenta
    } 
    */
}
